import React, { Component } from 'react';
import { BASE_URL } from 'Config';
import { Descriptions, Badge, Card, message, Spin, Image } from 'antd';
import {
	requestCategoryNameById,
	requestSearchProductById,
} from 'Api/backend-api';
import './Details.scss';

export default class Details extends Component {
	state = {
		product: {
			status: false,
			imgs: [],
			productName: '',
			categoryName: '',
			description: '',
			price: 0,
			details: '',
		},
		isLoading: true,
	};

	async componentDidMount() {
		const productInfo = await this.initProductInfo();
		this.setState({
			...productInfo,
			isLoading: false,
		});
	}

	initProductInfo = async () => {
		const product = this.props.location.state
			? this.getProductInfoFromHistory()
			: await this.retrieveProductInfo(this.props.match.params.id);
		const {
			status,
			imgs,
			name: productName,
			desc: description,
			price,
			detail: details,
			categoryId,
		} = product;
		const categoryName =
			categoryId && (await this.retrieveCategoryName(categoryId));
		return {
			imgs,
			productName,
			description,
			price,
			details,
			categoryName,
			status: status === 1,
		};
	};

	retrieveCategoryName = async categoryId => {
		const result = await requestCategoryNameById(categoryId);
		const { status, data } = result.data;
		if (status === 0 && data) {
			return data.name;
		} else {
			message.error('Request data failed');
		}
	};

	retrieveProductInfo = async id => {
		const result = await requestSearchProductById(id);
		const { status, data } = result.data;
		if (status === 0) {
			return data;
		} else {
			message.error('Request data failed');
		}
	};

	getProductInfoFromHistory = () => {
		const { history, location } = this.props;
		history.replace(location.pathname, undefined);
		return location.state.product;
	};

	render() {
		const {
			status,
			imgs = [],
			productName,
			description,
			price,
			categoryName,
			details,
			isLoading,
		} = this.state;
		return (
			<Card>
				<Spin spinning={isLoading}>
					<Descriptions bordered>
						<Descriptions.Item label='Name' span={3}>
							{productName}
						</Descriptions.Item>
						<Descriptions.Item label='Description' span={3}>
							{description}
						</Descriptions.Item>
						<Descriptions.Item label='Category'>
							{categoryName}
						</Descriptions.Item>
						<Descriptions.Item label='Price'>{`$${price}`}</Descriptions.Item>
						<Descriptions.Item label='Status'>
							<Badge
								status={status ? 'processing' : 'default'}
								text={status ? 'Selling' : 'Not selling'}
							/>
						</Descriptions.Item>
						
						<Descriptions.Item label='Images' span={3}>
							{imgs.map(img => (
								<Image
									// width={200}
									src={`${BASE_URL}/upload/${img}`}
									preview={true}
									key={img}
									placeholder={true}
									width='120px'
									height='120px'
									// TODO: remove fallback image to somewhere else
									// TODO: show something when images are loading
									fallback='data:image/png;base64,'
								/>
							))}
						</Descriptions.Item>
						<Descriptions.Item label='Details' span={3}>
							<div dangerouslySetInnerHTML={{ __html: details }}></div>
						</Descriptions.Item>
					</Descriptions>
				</Spin>
			</Card>
		);
	}
}
